  text-align: center;
}

.research-thumbnail {
  max-width: 100%;
  height: auto;
  border-radius: 10px;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.research-content {
  font-size: 1.1rem;
  line-height: 1.8;
}

.research-tags {
  margin-top: 2rem;
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.tag {
  background-color: #e9ecef;
  color: var(--muted-color);
  padding: 0.3rem 0.8rem;
  border-radius: 20px;
  font-size: 0.9rem;
}

.related-research {
  margin-top: 3rem;
}

.related-title {
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
