  height: 250px;
  border-radius: 50%;
  object-fit: cover;
  border: 5px solid var(--secondary-color);
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.about-bio {
  flex: 2 1 500px;
}

.about-name {
  font-size: 2rem;
  margin-bottom: 1rem;
}

.about-position {
  font-size: 1.2rem;
  color: var(--secondary-color);
  margin-bottom: 1rem;
}

.about-description {
  font-size: 1.1rem;
  margin-bottom: 1.5rem;
}

.about-interests {
  margin-top: 2rem;
}

.interests-list {
  list-style: none;
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.interest-item {
  background-color: #e9ecef;
  color: var(--muted-color);
  padding: 0.3rem 0.8rem;
  border-radius: 20px;
  font-size: 0.9rem;
