title: Contact
---

<div class="container">
  <section class="about-section">
    <h1>Contact Me</h1>
    <p class="contact-description">
      Feel free to get in touch with me for research collaborations, speaking opportunities, or any other inquiries.
    </p>

    <div class="contact-info">
      <div class="contact-item">
        <i class="fas fa-envelope contact-icon"></i>
        <span class="contact-text">
          <a href="mailto:youremail@example.com" class="contact-link">youremail@example.com</a>
        </span>
      </div>
      <div class="contact-item">
        <i class="fas fa-building contact-icon"></i>
        <span class="contact-text">[Institution/Company Name], [Department], [Address]</span>
      </div>
      <div class="contact-item">
        <i class="fas fa-phone contact-icon"></i>
        <span class="contact-text">[Phone Number]</span>
      </div>
      <div class="contact-item">
        <i class="fab fa-github contact-icon"></i>
        <span class="contact-text">
          <a href="https://github.com/yourusername" class="contact-link" target="_blank" rel="noopener noreferrer">github.com/yourusername</a>
        </span>
      </div>
      <div class="contact-item">
        <i class="fas fa-graduation-cap contact-icon"></i>
        <span class="contact-text">
          <a href="https://scholar.google.com/citations?user=youruserid" class="contact-link" target="_blank" rel="noopener noreferrer">Google Scholar</a>
        </span>
      </div>
      <div class="contact-item">
        <i class="fab fa-linkedin contact-icon"></i>
        <span class="contact-text">
          <a href="https://linkedin.com/in/yourusername" class="contact-link" target="_blank" rel="noopener noreferrer">linkedin.com/in/yourusername</a>
        </span>
      </div>
    </div>
  </section>

  <section class="about-section">
    <h2>Send Me a Message</h2>
    <form id="contact-form" class="contact-form">
      <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" id="name" name="name" required class="form-input">
      </div>
      <div class="form-group">
        <label for="email">Email:</label>
        <input type="email" id="email" name="email" required class="form-input">
      </div>
      <div class="form-group">
        <label for="subject">Subject:</label>
        <input type="text" id="subject" name="subject" required class="form-input">
      </div>
      <div class="form-group">
        <label for="message">Message:</label>
        <textarea id="message" name="message" rows="5" required class="form-input"></textarea>
      </div>
      <button type="submit" class="btn btn-primary">Send Message</button>
    </form>
  </section>
